import { HttpClientTestingModule } from '@angular/common/http/testing';
import { TestBed } from '@angular/core/testing';
import { of, ReplaySubject } from 'rxjs';
import { CountryService } from 'src/app/services/country.service';
var createSpyObj = jasmine.createSpyObj;
import { NavMenuComponent } from './nav-menu.component';
describe('OrderConfirmComponent', () => {
    let component;
    let fixture;
    let mockCountryService;
    let spyCountryService;
    const $currentCountry = new ReplaySubject();
    const mockCountries = [
        {
            countryId: 1,
            conversionRateFromAud: 1,
            currencyCode: 'AUD',
            name: 'Australia',
        },
        {
            countryId: 2,
            conversionRateFromAud: 0.67,
            currencyCode: 'EUR',
            name: 'France',
        },
    ];
    beforeEach(async () => {
        await TestBed.configureTestingModule({
            imports: [HttpClientTestingModule],
            declarations: [NavMenuComponent],
            providers: [
                { provide: 'BASE_URL', useValue: 'http://localhost' },
                {
                    provide: CountryService,
                    useValue: spyCountryService,
                },
            ],
        }).compileComponents();
    });
    beforeEach(() => {
        fixture = TestBed.createComponent(NavMenuComponent);
        component = fixture.componentInstance;
        spyCountryService = createSpyObj('CountryService', ['getAll']);
        spyCountryService.getAll.and.returnValue(of(mockCountries));
        spyCountryService = createSpyObj('CountryService', ['$currentCountry']);
        spyCountryService.$currentCountry.next(mockCountries[0]);
        // mockCountryService = TestBed.get(CountryService);
        // mockCountryService.getAll.and.returnValue(of(mockCountries));
        //mockCountryService.$currentCountry.and.returnValue(of(mockCountries[0]));
        fixture.detectChanges();
    });
    it('should create', () => {
        expect(component).toBeTruthy();
    });
});
