import { HttpClientTestingModule } from '@angular/common/http/testing';
import { TestBed } from '@angular/core/testing';
import { of, ReplaySubject } from 'rxjs';
import { CountryService } from 'src/app/services/country.service';
import { OrderConfirmComponent } from '../order-confirm/order-confirm.component';
import { NavMenuComponent } from './nav-menu.component';
describe('OrderConfirmComponent', () => {
    let component;
    let fixture;
    let $countryMock = new ReplaySubject();
    const spyObj = Object.assign(Object.assign({}, jasmine.createSpyObj('CountryService', ['getAll'])), { $country: $countryMock });
    const mockCountries = [
        {
            countryId: 1,
            conversionRateFromAud: 1,
            currencyCode: 'AUD',
            name: 'Australia',
        },
        {
            countryId: 2,
            conversionRateFromAud: 0.67,
            currencyCode: 'EUR',
            name: 'France',
        },
    ];
    beforeEach(async () => {
        let mockCountryService = {
            $country: $countryMock,
            getAll: () => jasmine.createSpyObj('CountryService', ['getAll']),
        };
        await TestBed.configureTestingModule({
            imports: [HttpClientTestingModule],
            declarations: [NavMenuComponent, OrderConfirmComponent],
            providers: [
                { provide: 'BASE_URL', useValue: 'http://localhost' },
                {
                    provide: CountryService,
                    useValue: mockCountryService,
                },
            ],
        }).compileComponents();
    });
    beforeEach(() => {
        fixture = TestBed.createComponent(NavMenuComponent);
        component = fixture.componentInstance;
        let countryService = TestBed.get(CountryService);
        countryService.getAll.and.returnValue(of(mockCountries));
        $countryMock.next(mockCountries[0]);
        fixture.detectChanges();
    });
    it('should create', () => {
        spyObj.spyMethod();
        expect(component).toBeTruthy();
    });
});
