import { TestBed } from '@angular/core/testing';
import { HttpClientTestingModule } from '@angular/common/http/testing';
import { ProductItemComponent } from './product-item.component';
import { By } from '@angular/platform-browser';
describe('ProductItemComponent', () => {
    let component;
    let fixture;
    beforeEach(async () => {
        await TestBed.configureTestingModule({
            imports: [HttpClientTestingModule],
            declarations: [ProductItemComponent],
            providers: [{ provide: 'BASE_URL', useValue: 'http://localhost' }],
        }).compileComponents();
    });
    const mockProduct = {
        productId: 1,
        description: 'test description',
        name: 'test name',
        price: 10,
    };
    const mockCountry = {
        countryId: 1,
        conversionRateFromAud: 1,
        currencyCode: 'AUD',
    };
    beforeEach(() => {
        fixture = TestBed.createComponent(ProductItemComponent);
        component = fixture.componentInstance;
        component.product = mockProduct;
        component.selectedCountry = mockCountry;
        fixture.detectChanges();
    });
    it('should create', () => {
        expect(component).toBeTruthy();
    });
    it('should display product information', () => {
        const h3 = fixture.debugElement.query(By.css('h3')).nativeElement;
        expect(h3.textContent).toEqual(component.product.name);
        const p_description = fixture.nativeElement.querySelector('p.description');
        expect(p_description.textContent).toEqual(component.product.description);
        const p_price = fixture.nativeElement.querySelector('p.price');
        expect(p_price.textContent.trim()).toEqual('Price : A$' + component.product.price.toFixed(2));
    });
});
